mapper.select:
SELECT F.*, U.*, TO_CHAR(F.CREATED_AT, 'YYYY-MM-DD HH24:MI:SS') AS updatedTime
FROM FREE_BOARD F
LEFT JOIN (
	SELECT *
	FROM USERS
) U ON F.USER_ID = U.USER_ID
WHERE F.BOARD_ID = #{boardId}
AND F.CATEGORY= #{category}

SELECT *
FROM FREE_BOARD_LIKE_LOG
WHERE USER_ID = #{userId}
  AND BOARD_ID = #{boardId}

mapper.update:
UPDATE FREE_BOARD
SET

				LIKES = LIKES + #{finalstatus}


				DISLIKES = DISLIKES + #{finalstatus}

WHERE BOARD_ID = #{boardId}

mapper.select:
SELECT V.*, TO_CHAR(V.CREATED_AT, 'YYYY-MM-DD HH24:MI:SS') AS updatedTime
FROM VET_ANSWER V 
WHERE V.VET_BOARD_ID = #{vetBoardId}

mapper.update:
		UPDATE VET_ANSWER
    	SET
	        REVIEW_TEXT = #{reviewText}
		WHERE VET_ID = #{vetId}

UPDATE VET_ANSWER
SET
	RATING = #{rating},
	COMMENTS = #{comments}
WHERE VET_ID = #{vetId}
