mapper.update:

update cart_item
set
	quantity = quantity + #{quantity}
where cart_id = #{cartId} and product_id = #{productId}


update cart_item
set
	check_yn = #{checkYn}
where cart_id = #{cartId} and product_id = #{productId}


update cart_item
set
	quantity = #{quantity}
where cart_id = #{cartId} and product_id = #{productId}

update orders set
refund_request_date = sysdate,
order_status = #{orderStatus}, updated_at = sysdate where order_id = #{orderId} and user_id = #{userId}

mapper.select:

select order_id, total_price, ordered_at, user_id, receiver_name, receiver_phone, receiver_addr, delivery_message
from orders
where user_id = #{userId} and order_id = #{orderId}

select o.order_id, o.total_price, o.order_status, o.ordered_at, o.user_id, o.receiver_name, o.receiver_phone, o.receiver_addr, o.delivery_message, p.product_name, p.filepath, od.product_id, od.quantity, od.price, od.refund_status from orders o join order_details od on o.order_id = od.order_id join ( select p.*, f.filepath from product p left join product_file f on p.product_id = f.product_id and f.thumbnail = 'Y' ) p on od.product_id = p.product_id where o.user_id = #{userId} and o.ordered_at between #{startDate} and TO_DATE('' || #{endDate} || '23:59:59', 'YYYY-MM-DD HH24:MI:SS')
and o.order_status = #{orderStatus}
order by o.ordered_at desc
