mapper.select:

	select user_agent_browser, count(*) visit_count from VISIT_LOG 
	WHERE 1=1

        AND  EXTRACT(YEAR FROM access_time) =  #{year} 


        AND  EXTRACT(MONTH FROM access_time) =  #{month} 


        AND  EXTRACT(HOUR FROM access_time) =  #{hour} 

	group by user_agent_browser


	SELECT 
	     TO_CHAR(TRUNC(access_time), 'YYYY-MM-DD')   AS visitdate, 
	    COUNT(*) AS visit_count 
	FROM visit_log
	WHERE 1=1

        AND  EXTRACT(YEAR FROM access_time) =  #{year} 


        AND  EXTRACT(MONTH FROM access_time) =  #{month} 


        AND  EXTRACT(HOUR FROM access_time) =  #{hour} 

	GROUP BY TRUNC(access_time)
	ORDER BY visitdate


	SELECT 
	    EXTRACT(HOUR FROM access_time) AS visithour, 
	    COUNT(*) AS visit_count 
	FROM visit_log
	WHERE 1=1

        AND  EXTRACT(YEAR FROM access_time) =  #{year} 


        AND  EXTRACT(MONTH FROM access_time) =  #{month} 


        AND  EXTRACT(HOUR FROM access_time) =  #{hour} 

	GROUP BY EXTRACT(HOUR FROM access_time)
	ORDER BY visithour

SELECT COUNT(CASE WHEN p.PAYMENT_ID IS NOT NULL THEN 1 END) AS PURCHASED_MEMBERS, COUNT(CASE WHEN p.PAYMENT_ID IS NULL THEN 1 END) AS NON_PURCHASED_MEMBERS FROM users u LEFT JOIN PAYMENT p ON u.USER_ID = p.USER_ID WHERE 1=1
AND EXTRACT(YEAR FROM u.CREATED_AT) = #{year}
AND EXTRACT(MONTH FROM u.CREATED_AT) = #{month}
AND EXTRACT(HOUR FROM u.CREATED_AT) = #{hour}

	select P.*  , 
	(
		SELECT COUNT(*)
	    FROM Product P 
	    WHERE 1=1

	        AND  EXTRACT(YEAR FROM P.REGISTRATION_DATE) =  #{year} 


	        AND  EXTRACT(MONTH FROM P.REGISTRATION_DATE) =  #{month} 


	        AND  EXTRACT(HOUR FROM P.REGISTRATION_DATE) =  #{hour} 

    ) AS productTotalCnt
	FROM Product P
	WHERE 1=1

        AND  EXTRACT(YEAR FROM P.REGISTRATION_DATE) =  #{year} 


        AND  EXTRACT(MONTH FROM P.REGISTRATION_DATE) =  #{month} 


        AND  EXTRACT(HOUR FROM P.REGISTRATION_DATE) =  #{hour}

	select P.*  , 
	(
		SELECT COUNT(*)
        FROM payment P 
        where nvl(P.subscription_period,0) = 0

	        AND  EXTRACT(YEAR FROM PAYMENT_DATE) =  #{year} 


	        AND  EXTRACT(MONTH FROM PAYMENT_DATE) =  #{month} 


	        AND  EXTRACT(HOUR FROM PAYMENT_DATE) =  #{hour} 

     ) AS paymentTotalCnt
	FROM payment P
	WHERE 1=1

        AND  EXTRACT(YEAR FROM PAYMENT_DATE) =  #{year} 


        AND  EXTRACT(MONTH FROM PAYMENT_DATE) =  #{month} 


        AND  EXTRACT(HOUR FROM PAYMENT_DATE) =  #{hour} 

    AND nvl(P.subscription_period,0) = 0

	select U.*  , 
	(
		SELECT COUNT(*)
        FROM USERS
        WHERE 1=1

	        AND  EXTRACT(YEAR FROM CREATED_AT) =  #{year} 


	        AND  EXTRACT(MONTH FROM CREATED_AT) =  #{month} 


	        AND  EXTRACT(HOUR FROM CREATED_AT) =  #{hour} 

     ) AS userTotalCnt
	FROM USERS U
	WHERE 1=1

        AND  EXTRACT(YEAR FROM CREATED_AT) =  #{year} 


        AND  EXTRACT(MONTH FROM CREATED_AT) =  #{month} 


        AND  EXTRACT(HOUR FROM CREATED_AT) =  #{hour}

	select D.*  , 
	(
		SELECT COUNT(*)
    	FROM donation_history
    	WHERE 1=1

	        AND  EXTRACT(YEAR FROM DONATION_DATE) =  #{year} 


	        AND  EXTRACT(MONTH FROM DONATION_DATE) =  #{month} 


	        AND  EXTRACT(HOUR FROM DONATION_DATE) =  #{hour} 

     ) AS donationTotalCnt
	FROM donation_history D
	WHERE 1=1

        AND  EXTRACT(YEAR FROM DONATION_DATE) =  #{year} 


        AND  EXTRACT(MONTH FROM DONATION_DATE) =  #{month} 


        AND  EXTRACT(HOUR FROM DONATION_DATE) =  #{hour}

    SELECT SEARCH_TERM, COUNT(*) AS SEARCH_COUNT
    FROM search_history
    where 1=1

            USER_ID = #{userId}


	        AND  EXTRACT(YEAR FROM SEARCHED_AT) =  #{year} 


	        AND  EXTRACT(MONTH FROM SEARCHED_AT) =  #{month} 


	        AND  EXTRACT(HOUR FROM SEARCHED_AT) =  #{hour} 

    GROUP BY SEARCH_TERM
    ORDER BY SEARCH_COUNT DESC
    OFFSET 0 ROWS FETCH NEXT 5 ROWS ONLY

mapper.delete:
UPDATE FREE_BOARD
SET IS_DELETED = 'Y'
WHERE BOARD_ID IN 

	#{item}

AND CATEGORY = #{category}

mapper.select:

SELECT *
FROM Product
Where Product_id = #{productId}

SELECT p.PRODUCT_ID, p.PRODUCT_NAME, p.PRICE, SUM(od.QUANTITY) AS TOTAL_SOLD FROM order_details od JOIN orders o ON od.ORDER_ID = o.ORDER_ID JOIN product p ON od.PRODUCT_ID = p.PRODUCT_ID WHERE o.ORDER_STATUS IN ('paid', 'completed') AND o.REFUND_STATUS = 'N'
AND o.ORDERED_AT BETWEEN #{startDate} AND #{endDate}
AND o.ORDERED_AT = #{startDate}
GROUP BY p.PRODUCT_ID, p.PRODUCT_NAME, p.PRICE ORDER BY TOTAL_SOLD DESC OFFSET 0 ROWS FETCH FIRST 6 ROWS ONLY

SELECT SUM(od.PRICE) AS totalSales, COUNT(DISTINCT o.ORDER_ID) AS totalOrders, CASE WHEN COUNT(DISTINCT o.ORDER_ID) > 0 THEN ROUND(SUM(od.PRICE) / COUNT(DISTINCT o.ORDER_ID)) ELSE 0 END AS averageOrder FROM order_details od JOIN orders o ON od.ORDER_ID = o.ORDER_ID WHERE o.ORDER_STATUS IN ('paid', 'completed')
AND o.ORDERED_AT BETWEEN #{startDate} AND #{endDate}
AND o.ORDERED_AT = #{startDate}

SELECT * FROM order_details od LEFT JOIN product p on od.product_id = p.product_id LEFT JOIN product_file pf on p.product_id = pf.product_id and pf.thumbnail = 'Y' WHERE 1=1
AND order_id = #{orderId}
ORDER BY order_detail_id DESC

mapper.update:
	UPDATE order_details
	SET 

		quantity = #{quantity},


		price = #{price},


		tracking_number = #{trackingNumber},


		refund_status = #{refundStatus},

          product_id =   #{productId} 
	WHERE order_detail_id = #{orderDetailId}
	  AND product_id =   #{productId}
