mapper.update:
UPDATE PRODUCT_REVIEWS
SET
	TITLE = #{title},
	REVIEW_TEXT = #{reviewText},
	 RATING = #{rating}
WHERE REVIEW_ID = #{reviewId}

mapper.select:
SELECT P.PRODUCT_ID, P.PRODUCT_NAME, R.REVIEW_ID, R.REVIEW_TEXT, R.RATING ,R.USER_ID, R.TITLE, R.CREATED_AT ,R.HELPCNT,R.DELETE_YN ,F.* FROM PRODUCT P INNER JOIN PRODUCT_REVIEWS R ON P.PRODUCT_ID = R.PRODUCT_ID LEFT JOIN REVIEW_FILE F ON R.REVIEW_ID = F.REVIEW_ID AND F.THUMB_YN = 'Y' WHERE 1=1
AND P.PRODUCT_ID = #{productId}
ORDER BY CREATED_AT DESC OFFSET #{page} ROWS FETCH NEXT #{pageSize} ROWS ONLY

SELECT COUNT(*) FROM PRODUCT_REVIEWS WHERE 1=1
AND PRODUCT_ID = #{productId}
AND DELETE_YN != 'Y'

SELECT COUNT(*) 
FROM PRODUCT_REVIEWS
WHERE REVIEW_ID = #{reviewId} 
  AND USER_ID = #{userId, jdbcType=VARCHAR}

mapper.update:
	    UPDATE PRODUCT_REVIEWS
	    	SET HELPCNT = HELPCNT + 1
    	WHERE REVIEW_ID = #{reviewId}

mapper.select:
SELECT * FROM PRODUCT_QNA WHERE 1=1
AND PRODUCT_ID = #{productId}
ORDER BY QNA_ID DESC OFFSET #{page} ROWS FETCH NEXT #{pageSize} ROWS ONLY

mapper.delete:
		DELETE 
		FROM PRODUCT_QNA 
		WHERE QNA_ID = #{qnaId} 
      		AND USER_ID = #{userId}

mapper.update:
UPDATE PRODUCT_QNA
SET
	TITLE = #{title},
	QUESTION_TEXT = #{questionText}
WHERE QNA_ID = #{qnaId}
