mapper.select:
SELECT *
FROM USERS
WHERE USER_ID = #{userId} AND PASSWORD = #{pwd}

mapper.update:
  UPDATE USERS
	SET
	PASSWORD = #{pwd}
	WHERE USER_ID = #{userId}

mapper.select:
SELECT USER_ID,USERNAME,NICKNAME,EMAIL,PROFILE_IMAGE,PHONE_NUMBER,BIRTH_DATE, ADDRESS,ROLE,GENDER
FROM USERS
WHERE USER_ID = #{userId}
