--  DDL for View USER_NOTIFICATIONS
--------------------------------------------------------

  CREATE OR REPLACE FORCE EDITIONABLE VIEW "SYSTEM"."USER_NOTIFICATIONS" ("ID", "MESSAGE", "BOARD_ID", "VET_BOARD_ID", "ORDER_ID", "QNA_ID", "READ_YN", "CREATED_AT", "PARENT_COMMENT_ID", "REVIEW_ID", "COMMENT_ID", "PRODUCT_ID", "USER_ID") AS 
  SELECT n."ID",n."MESSAGE",n."BOARD_ID",n."VET_BOARD_ID",n."ORDER_ID",n."QNA_ID",n."READ_YN",n."CREATED_AT",n."PARENT_COMMENT_ID",n."REVIEW_ID", n."COMMENT_ID" , n."PRODUCT_ID", COALESCE(b.user_id, v.user_id, o.user_id, q.user_id , c.user_id , r.user_id) AS user_id
FROM notifications n
LEFT JOIN free_board b ON n.board_id = b.board_id AND n.parent_comment_id is null
LEFT JOIN vet_board v ON n.vet_board_id = v.vet_board_id AND n.message  like '%게시글%'
LEFT JOIN orders o ON n.order_id = o.order_id
LEFT JOIN product_qna q ON n.qna_id = q.qna_id
LEFT JOIN free_board_comment c ON n.parent_comment_id = c.comment_id
LEFT JOIN ( select * from vet_answer v left join veterinarian u on v.vet_id=u.vet_id ) r ON n.review_id = r.review_id
;
REM INSERTING into SYSTEM.USER_NOTIFICATIONS
SET DEFINE OFF;
